from fluidity_tools import stat_parser import numpy as np
s = stat_parser('particle_attributes.particles.Steve') attributes_error_2 = np.zeros((1,100)) X = np.fromfile('Xvals.txt',sep=' ') Y = np.fromfile('Yvals.txt',sep=' ')
for i in range(100): name = 'Steve_' + str(i+1).zfill(3) attributes_error_2[0,i] = s[name]['Chem1'][0][-1] - (X[i] + 8.0) print "max error",abs(attributes_error_2).max()

